from machine import I2C,UART,Pinfrom  Sengo1  import *import timeimport randomcolor_Name = [" ","Black","Whiet","Red","Green","Blue","Yellow"]#Wait for Sengo1 to  initialize the operating system. This waiting time cannot be removed to prevent the situation where the controller has already developed and sent instructions before Sengo1 has been fully initializedtime.sleep(3)# Select UART or I2C communication mode. Sengo2 is I2C mode by default. You can change it by just pressing the button.# 4 UART communication modes: UART9600(Standard Protocol Instruction); UART57600(Standard Protocol Instruction), UART115200(Standard Protocol Instruction); Simple9600(Simple Protocol Instruction)########################################################################################################## port = UART(2,rx=Pin(16),tx=Pin(17),baudrate=9600)port = I2C(0,scl=Pin(21),sda=Pin(20),freq=400000)# Sengo1 communication address: 0x60. If multiple devices are connected to the I2C bus, please avoid address conflicts.sengo1 = Sengo1(0x60)err = sengo1.begin(port)if err != SENTRY_OK:    print(f"Initialization failed，error code:{err}")else:    print("Initialization succeeded")# 1、算法的默认参数设置，如无需更改参数，则可移除本代码块；# 2、只有颜色识别与色块检测两种算法需要设置参数；# 3、Sengo1图像的长宽比为4:3，当宽高值设为3:4时，识别区域为正方形；当宽高值设为1:1时，识别区域为长方形sengo1.SetParam(sengo1_vision_e.kVisionColor,[50, 50, 3, 4, 1])time.sleep(0.1)#Sengo1每次只能运行一种识别算法；err = sengo1.VisionBegin(sengo1_vision_e.kVisionColor)if err != SENTRY_OK:    print(f"Starting algo Color failed，error code:{err}")else:    print("Starting algo Color succeeded")       while True:       # Sengo1不主动返回检测识别结果，需要主控板发送指令进行读取。读取的流程：首先读取识别结果的数量，接收到指令后，Sengo1会刷新结果数据，如果结果数量不为零，那么主控再发送指令读取结果的相关信息。请务必按此流程构建程序。    # Sengo最多输出1个识别结果。    obj_num = (sengo1.GetValue(sengo1_vision_e.kVisionColor, sentry_obj_info_e.kStatus))    if obj_num:        #读取颜色标签值        label = sengo1.GetValue(sengo1_vision_e.kVisionColor,sentry_obj_info_e.kLabel)        #输出标签值与颜色名称        print(f"Label:{label}  Color:{color_Name[label]}")        time.sleep(0.2)